mapper.select:
	SELECT *
    FROM culture
    WHERE ctgry_three_nm = #{ctgry_three_nm}
    AND ctprvn_nm = #{ctprvn_nm}
    order by SIGNGU_NM

	SELECT *
    FROM culture
    WHERE ctgry_three_nm = #{ctgry_three_nm}
    AND ctprvn_nm = #{ctprvn_nm}
    AND signgu_nm = #{signgu_nm}
    order by SIGNGU_NM, legaldong_nm

	SELECT *
    FROM culture
    WHERE ctgry_three_nm = #{ctgry_three_nm}
    AND ctprvn_nm = #{ctprvn_nm}
    AND signgu_nm = #{signgu_nm}
    AND legaldong_nm =#{legaldong_nm}
    order by signgu_nm, legaldong_nm
